import React, {useState, useEffect, useContext } from 'react';
import './App.css';
import { decisionMaker } from './decisionMaker';
// import { LocaleContext } from './context';

export default function InfinityForm() {
    const [firstName, setFirstName] = useState('Leroy');
    const [lastName, setLastName] = useState('Jenkins');
    const [status, setStatus] = useState('');
    const [applied, setApplied] = useState(false);
    // const [locale] = useContext(LocaleContext);

    useEffect(() => {
        document.title = firstName + "'s Application";
    });

    function handleFirstNameChange(e) {
        setFirstName(e.target.value)
    }

    function handleLastNameChange(e) {
        setLastName(e.target.value)
    }

    function apply() {
        setStatus(decisionMaker());
        setApplied(true)
    }

    function reset() {
        setFirstName('');
        setLastName('');
        setStatus('');
        setApplied(false)
    }


    if (applied) {
        return (
            <section>
                <h1>Da Future Application Status:</h1>
                <h2>{firstName} {lastName} was {status}</h2>

                <button onClick={reset}>
                    Reset
                </button>
                {/* <h2>{locale}</h2> */}
            </section>
        )
    }
    return (
        <section>
            <h1>Excella Application</h1>
            <div>
                <input
                    value={firstName}
                    onChange={handleFirstNameChange}
                />
            </div>
            <div>
                <input
                    value={lastName}
                    onChange={handleLastNameChange}
                />
            </div>
            <button onClick={apply}>
                Apply
            </button>
        </section>
    );

}
